mapper.select:
select count(user_acct)
from t_sys_user
where is_deleted = 0
and user_acct = #{driverAcct}

    and user_code != #{driverCode}

select count(user_phone)
from t_sys_user
where is_deleted = 0
and user_phone = #{driverPhone}

    and user_code != #{driverCode}

select a.user_code driverCode, a.user_name driverName, a.user_phone driverPhone, a.user_idcard driverIdCard, a.user_acct driverAcct, a.version version from t_sys_user a left join t_driver b on (a.user_code = b.driver_code) where a.is_deleted = 0
and a.user_code like concat('%', #{driverCode}, '%')
and a.user_name like concat('%', #{driverName}, '%')
and b.province_id = #{provinceId}
and b.city_id = #{cityId}
and b.district_id = #{districtId}
order by a.create_user desc
